public class 제어문_반복문_for4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int i=1;i<=6;i++)
        {
        	if(i%2==0)
        	{
        	   for(int j=1;j<=3;j++)
        	   {
        		   System.out.print("*");
        	   }
        	}
        
        	else
        	{
        	   for(int j=1;j<=2;j++)
         	   {
         		   System.out.print("*");
         	   }
        	}
             
            System.out.println();
        }
	}

}
